FlowNode.ExtendedProperty:
ZoneType=0;ScriptContent=call_map = _gd_['call_map'] # chan => call_info
conf_map = _gd_['conf_map'] # res_id => call_info

call_map_r = _gd_['call_map_r'] # res_id => call_info
conf_map_r = _gd_['conf_map_r'] # res_id -> conf_info

last_err = None
try:
	conf_info = call_info = None
	try:
		conf_info = conf_map[conf_id]
	except KeyError as e:
		TraceErr('conference id <%s> NOT existed' % e)
	try:
		call_info = call_map[chan]
	except KeyError as e:
		TraceErr('call channel <%s> NOT existed' % e)
	if conf_info and call_info:
		res_id = conf_info['res_id']
		call_res_id = call_info['res_id']
		jsonrpc.send_method(
			method='ivr.conf.on_exited',
			params={
				'res_id': res_id,
				'call_res_id': call_res_id
			}
		)
except IvrError as err:
	TraceErr(err)
	last_err = err
